mapper.select:
SELECT A.OBJ_TAB_FIELD_ID AS objTabFieldId, A.OBJ_CONDITION AS objCondition, A.OBJ_KEY AS objKey, B.TAB_FIELD_COMMENT AS fieldComment, B.TAB_FIELD_NAME AS fieldName, B.TAB_IS_NULL AS fieldIsNull, B.TAB_IS_KEY AS fieldIsKey FROM SYS_OBJ_TAB_FIELD A LEFT JOIN SYS_TAB_FIELD B ON(A.OBJ_FIELD_ID=B.TAB_FIELD_ID)
1=1
and A.OBJ_TAB_FIELD_ID = #{objTabFieldId,jdbcType=VARCHAR}
and A.OBJ_TAB_ID = #{objTabId,jdbcType=VARCHAR}
and A.OBJ_FIELD_ID = #{objFieldId,jdbcType=VARCHAR}
and B.TAB_IS_KEY = #{fieldIsKey,jdbcType=VARCHAR}
and A.OBJ_KEY = #{objKey,jdbcType=VARCHAR}

SELECT A.OBJ_TAB_FIELD_ID AS objTabFieldId, A.OBJ_CONDITION AS objCondition, C.TAB_FIELD_COMMENT AS fieldComment, C.TAB_FIELD_TYPE AS tabFieldType, A.OBJ_FIELD_ID AS objFieldId, A.OBJ_KEY AS objKey FROM SYS_OBJ_TAB_FIELD A LEFT JOIN SYS_OBJ_TAB B ON(A.OBJ_TAB_ID=B.OBJ_TAB_ID) LEFT JOIN SYS_TAB_FIELD C ON(A.OBJ_FIELD_ID=C.TAB_FIELD_ID)
1=1
and B.OBJ_ID = #{objId,jdbcType=VARCHAR}
and A.OBJ_TAB_ID = #{objTabId,jdbcType=VARCHAR}

mapper.delete:

delete from SYS_OBJ_TAB_FIELD
where
1=1

  AND OBJ_TAB_FIELD_ID = #{objTabFieldId,jdbcType=VARCHAR}


  AND OBJ_TAB_ID = #{objTabId,jdbcType=VARCHAR}
