SELECT A.ROLE_ID AS roleId, A.ROLE_CODE AS roleCode, A.ROLE_NAME AS roleName, A.ROLE_TYPE AS roleType, A.ROLE_STATUS AS roleStatus, A.ROLE_RANGE AS roleRange, A.CREATE_TIME AS createTime, B.USER_NAME AS createUser, C.DICTIONARY_NAME AS rangeName FROM SYS_ROLE A LEFT JOIN SYS_USER B ON(A.CREATER = B.USER_CODE) LEFT JOIN SYS_DICTIONARY C ON(A.ROLE_RANGE=C.DICTIONARY_CODE) AND C.DICTIONARY_TYPE = '01' LEFT JOIN SYS_ROLE_ORG D ON(A.ROLE_ID=D.ROLE_ID)
1=1
and A.ROLE_CODE LIKE '%' #{roleCode,jdbcType=VARCHAR} '%'
and A.ROLE_NAME LIKE '%' #{roleName,jdbcType=VARCHAR} '%'
and A.ROLE_TYPE = #{roleType,jdbcType=VARCHAR}
and A.ROLE_STATUS = #{roleStatus,jdbcType=VARCHAR}
and B.USER_NAME = #{createUser,jdbcType=VARCHAR}
and A.CREATE_TIME >= #{startDate,jdbcType=VARCHAR}
and A.CREATE_TIME <= #{endDate,jdbcType=VARCHAR}
and D.ORG_ID = #{orgId,jdbcType=VARCHAR}
LIMIT #{pageStart},#{pageEnd}

SELECT
  count(*) count
FROM
SYS_ROLE A
LEFT JOIN SYS_USER B ON(A.CREATER = B.USER_CODE)

  1=1

    and A.ROLE_CODE LIKE '%' #{roleCode,jdbcType=VARCHAR} '%'


    and A.ROLE_NAME LIKE '%' #{roleName,jdbcType=VARCHAR} '%'


    and A.ROLE_TYPE = #{roleType,jdbcType=VARCHAR}


    and A.ROLE_STATUS = #{roleStatus,jdbcType=VARCHAR}


    and B.USER_NAME = #{createUser,jdbcType=VARCHAR}


    and A.CREATE_TIME  >=   #{startDate,jdbcType=VARCHAR}


    and A.CREATE_TIME  <=   #{endDate,jdbcType=VARCHAR}
