mapper.select:
select A.ACCOUNT_ID AS accountId, A.USER_ID AS userId, A.LOGIN_NAME AS loginName, A.STATUS AS status, A.ACCOUNT_LOCK AS accountLock, B.USER_NAME AS userName, B.USER_CODE AS userCode, B.USER_TEL AS userTel, B.USER_EMAIL AS userEmail, C.ORG_ID AS orgId, D.PARENT_ID AS parentId, D.ORG_NO AS orgNo from SYS_USER_ACCOUNT A LEFT JOIN SYS_USER B ON(A.USER_ID = B.USER_ID) LEFT JOIN SYS_USER_ORG C ON(A.USER_ID=C.USER_ID) LEFT JOIN SYS_ORG D ON(C.ORG_ID=D.ORG_ID)
1=1
and (A.LOGIN_NAME = #{loginName,jdbcType=VARCHAR} OR B.USER_CODE = #{loginName,jdbcType=VARCHAR} OR B.USER_EMAIL = #{loginName,jdbcType=VARCHAR} OR B.USER_TEL = #{loginName,jdbcType=VARCHAR})
and A.PASSWORD = #{password,jdbcType=VARCHAR}
and A.USER_ID = #{userId,jdbcType=VARCHAR}

mapper.delete:

delete from SYS_USER_ACCOUNT
where

  ACCOUNT_ID = #{accountId,jdbcType=VARCHAR}


  USER_ID = #{userId,jdbcType=VARCHAR}
